import React, { useEffect, useState } from 'react';
import ChartRow from './ChartRow';

function Chart (){
    const [info, setInfo] = useState(null);
    const [product, setProducts] = useState();
    console.log(product);
  useEffect(() => {
    const allInfo = async () => {
      let resProducts = await fetch(`http://localhost:5001/api/products/`);
      let productsSaved = await resProducts.json();
      let products = productsSaved.products
      console.log(products);
      if (productsSaved.meta.totalPages !== 1) {
        let iniciador = 1;
        let totalPages = productsSaved.meta.totalPages;
        while (iniciador <= totalPages ) {
            iniciador ++;
            let resProduct2 = await fetch(`http://localhost:5001/api/products/?page=${iniciador}`);
            let productsSaved2 = await resProduct2.json();
            let pro =  productsSaved2.products
            products.push(...pro)
        }
        setProducts(products)
      }else{
      setProducts(products)
    }
    }
    allInfo();
  }, []);
  useEffect(()=>{
    setInfo(product);
  }, [product]);

    return (
        /* <!-- DataTales Example --> */
        <div className="card shadow mb-4">
            <div className="card-body">
                <div className="table-responsive">
                    <table className="table table-bordered" id="dataTable" width="100%" cellSpacing="0">
                        <thead>
                            <tr>
                                <th>Nombre</th>
                                <th>Duración</th>
                                <th>Rating</th>
                                <th>Género</th>
                                <th>Premios</th>
                            </tr>
                        </thead>
                        <tfoot>
                            <tr>
                                <th>Título</th>
                                <th>Duración</th>
                                <th>Rating</th>
                                <th>Género</th>
                                <th>Premios</th>
                            </tr>
                        </tfoot>

                        <tbody>
                        {
                            info.map((row, i) => {
                                return <ChartRow {...row} key={i}/>
                            })
                            }
                         /{products &&
                products.products.map((product, i) => {
                  return <ProductsChartRow {...product} key={i} />;
                })}
                        </tbody>

                    </table>
                </div>
            </div>
        </div>

    )
}

export default Chart;
