import React, { useState, useEffect } from "react";
import CategoriesCount from "./CategoriesCount";

function GenresInDb() {
  const [info, setInfo] = useState(null);
  const [categoryCount, setCategories] = useState();
  
  console.log(info);
  useEffect(() => {
    const allInfo = async () => {
      let resProducts = await fetch(`http://localhost:5001/api/products/`);
      let productsSaved = await resProducts.json();
      let countByCategories = productsSaved.meta.countByCategory;
      let categoria = []
      console.log(categoria);
      //console.log(categoria);
      for (const key in countByCategories) {
        categoria.push(key+ "Cantidad: "+countByCategories[key])
        };
       
        
      
      //setNameCategories(nameByCategories);
      setCategories(categoria);
      
      
    }
    allInfo();
  }, []);
  
  useEffect(()=>{
    setInfo({categoryCount});
  }, [categoryCount]);

    return (
      <div className="col-lg-6 mb-4">
        <div className="card shadow mb-4">
          <div className="card-header py-3">
          <h5 className="m-0 font-weight-bold text-gray-800">
            Todas las Categorias        
            </h5>
          </div> 
          <div className="card-body">
            <div className="row">
              <div className="col-lg-6 mb-4">
                <div className="card bg-dark text-white shadow">
                {  categoryCount && <div className="card-body">  Cantidad: {categoryCount[0]} </div>}
                </div>
              </div>
              <div className="col-lg-6 mb-4">
                <div className="card bg-dark text-white shadow">
                {  categoryCount && <div className="card-body">  Cantidad: {categoryCount[1]}</div>}
                </div>
              </div>
              <div className="col-lg-6 mb-4">
                <div className="card bg-dark text-white shadow">
                {  categoryCount &&<div className="card-body">  Cantidad: {categoryCount[2]}</div>}
                </div>
              </div>
              <div className="col-lg-6 mb-4">
                <div className="card bg-dark text-white shadow">
                {  categoryCount && <div className="card-body">  Cantidad: {categoryCount[3]}</div>}
                </div>
              </div>
              <div className="col-lg-6 mb-4">
                <div className="card bg-dark text-white shadow">
                {  categoryCount && <div className="card-body">  Cantidad: {categoryCount[4]}</div>}
                </div>
              </div>
                </div>
              </div>
            </div>
          </div>
    );
}

export default GenresInDb;
