import React, { useState, useEffect } from "react";
import SmallCard from "./SmallCard";

/*  Cada set de datos es un objeto literal */

/* <!-- Movies in DB --> */

function ContentRowMovies() {
  const [info, setInfo] = useState(null);
  const [products, setProducts] = useState();
  const [users, setUsers] = useState();
  const [categories, setCategories] = useState();


  useEffect(() => {

    const allInfo = async () => {
      let resProducts = await fetch(`http://localhost:5001/api/products/`);
      let productsSaved = await resProducts.json();
      // console.log(productsSaved);

      // title.push("Total de productos");
      // setTitle(title);
      // color.push("warning");
      // setColor(color);
      //   return products;

      let resUsers = await fetch(`http://localhost:5001/api/users/`);
      let usersSaved = await resUsers.json();
      

      setProducts({
        title: "Total de productos",
        quantity: productsSaved.meta.count,
        color: "warning",
        icon: "fa-clipboard-list"
      });
      setUsers({
        title: "Total de usuarios",
        quantity: usersSaved.meta.count,
        color: "primary",
        icon: "fa-users"
      });
      setCategories({
        title: "Total de categorías",
        quantity: categoriesSaved.meta.categoriesCount,
        color: "danger",
        icon: "fa-asterisk"
      });
    };

    allInfo();
  }, []);

  useEffect(() => {
    // console.log(products);
    setInfo({ products, users, categories, subcategories, brands, orders });
  }, [products, users, categories, subcategories, brands, orders]);

  return (
    <div className="row">
      {/* {console.log(info)} */}
      {info && //why not info??????
        Object.values(info).map((category, i) => {
          return <SmallCard {...category} key={i} />;
        })}
    </div>
  );
}

export default ContentRowMovies;
