import React, { useState, useEffect } from "react";
import SmallCard from "./SmallCard";


function ContentRowMovies() {
    const [info, setInfo] = useState(null);
    const [products, setProducts] = useState();
    const [users, setUsers] = useState();
    const [categories, setCategories] = useState();


  useEffect(() => {

    const allInfo = async () => {
      let resProducts = await fetch(`http://localhost:5001/api/products/`);
      let productsSaved = await resProducts.json();
      console.log(productsSaved);


      // title.push("Total de productos");
      // setTitle(title);
      // color.push("warning");
      // setColor(color);
      //   return products;

      let resUsers = await fetch(`http://localhost:5001/api/users/`);
      let usersSaved = await resUsers.json();
      

      setProducts({
        title: "Total de productos",
        quantity: productsSaved.meta.count,
        color: "warning",
        icon: "fa-clipboard-list"
      });
      setUsers({
        title: "Total de usuarios",
        quantity: usersSaved.meta.count,
        color: "primary",
        icon: "fa-users"
      });
      setCategories({
        title: "Total de categorÃ­as",
        quantity: productsSaved.meta.categoriesCount,
        color: "danger",
        icon: "fa-asterisk"
      });
    };

    allInfo();
  }, []);

  useEffect(() => {
    setInfo({ products, users, categories});
  }, [products, users, categories]);

  return (
    <div className="row">
      {info && 
        Object.values(info).map((category, i) => {
          return <SmallCard {...category} key={i} />;          
        })}
    </div>
  );
}

export default ContentRowMovies;
function LastProductInDb(props) {
    const [product, setProduct] = useState();
  
    useEffect(() => {
      const getInfo = async () => {
        let resProduct = await fetch(`http://localhost:3001/api/products/last`);
        let productSaved = await resProduct.json();
        // console.log(productSaved);
  
        let lastProduct = productSaved;
        // console.log(lastProduct);
  
        setProduct(lastProduct);
      };
      getInfo();
    }, []);
  
    return (
      <React.Fragment>
        <div className="col-lg-6 mb-4">
          <div className="card shadow mb-4">
            <div className="card-header py-3">
              <h5 className="m-0 font-weight-bold text-gray-800">
                Last product in Data Base
              </h5>
            </div>
            {product && (
              <div className="card-body">
                <div className="text-center">
                  <img
                    className="img-fluid px-3 px-sm-4 mt-3 mb-4"
                    style={{ width: 40 + "rem" }}
                    src={`http://localhost:3001/images/products/${product.product.images[0].name}`}
                    alt=" Last product "
                  />
                </div>
                <p>{product.product.description}</p>
                <Link
                  className="btn btn-danger"
                  rel="nofollow"
                  to="/ProductDetail"
                >
                  View product detail
                </Link>
              </div>
            )}
          </div>
        </div>
  
        {/* <Switch> 
              <Route path="/ProductDetail"> 
                  {product && <ProductDetail product={product.product}/>}
              </Route> 
              <Route component={NotFound} />
          </Switch> */}
      </React.Fragment>
    );
  }
